import axios from "axios";
import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";

export const EditPet = (props) => {
    const {id} = useParams();

    const [name, setName] = useState('');
    const [type, setType] = useState('');
    const [description, setDescription] = useState('');
    const [skill1, setSkill1] = useState('');
    const [skill2, setSkill2] = useState('');
    const [skill3, setSkill3] = useState('');
    const [errors, setErrors] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        axios.get(`http://localhost:8000/api/pets${id}`)
            .then(res => {
                const {
                    title,
                    description,
                    price
                } = res.data

                setTitle(title);
                setPrice(price);
                setDescription(description);
            }).catch(err => {
                console.log(err);
            })
    }, [id])

    const handleUpdate = (e) => {
        e.preventDefault();
        const updatedProduct = {
        title,
        price,
        description
        }

        // setTitle('');
        // setPrice('');
        // setDescription('');

        axios.put(`http://localhost:8000/${id}/edit`, updatedProduct)
        .then(res => {
            navigate(`/${id}`);
        }).catch(err => {
            /*
            For this catch to happen, the server needs to return an error status code.
            `?.` is optional chaining to safely access keys that may not exist.
            `errors` exists on validation errors but maybe not on other errors.
            */
            console.log(err.response);
            setErrors(err.response?.data?.errors)
        })
    }

    return (
        <div className="w-50 p-4 rounded mx-auto shadow">
            <div>
                <h3 className="text-center">Edit Product</h3>

                <form
                    onSubmit={e => { handleUpdate(e) }}
                >
                    <div className="form-group">
                    <label className="h6">Title</label>
                    {
                        errors?.title && (
                        <p style={{ color: 'red' }}>{errors.title?.message}</p>
                        )
                    }
                    <input
                        onChange={(event) => {
                        setTitle(event.target.value);
                        }}
                        type="text" className="form-control" value={title}
                    />
                    </div>

                    <div className="form-group">
                    <label className="h6">Description</label>
                    {
                        errors?.description && (
                        <p style={{ color: 'red' }}>{errors.description?.message}</p>
                        )
                    }
                    <textarea
                        onChange={(event) => {
                        setDescription(event.target.value);
                        }}
                        type="text"
                        className="form-control" value={description}
                    ></textarea>
                    </div>

                    <div className="form-group">
                    <label className="h6">Price</label>
                    {
                        errors?.price && (
                        <p style={{ color: 'red' }}>{errors.price?.message}</p>
                        )
                    }
                    <input
                        onChange={(event) => {
                        setPrice(event.target.value);
                        }}
                        type="text"
                        className="form-control" value={price}
                    />
                    </div>
                    <button className="btn btn-sm btn-outline-success">Submit</button>
                </form>
            </div>
        </div>
    )
};

export default EditPet;
