import axios from "axios";
import { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";

export const NewPet = (props) => {
    const [name, setName] = useState('');
    const [type, setType] = useState('');
    const [description, setDescription] = useState('');
    const [skill1, setSkill1] = useState('');
    const [skill2, setSkill2] = useState('');
    const [skill3, setSkill3] = useState('');
    const [errors, setErrors] = useState(null);
    const navigate = useNavigate();

    const createPet = async (e) => {
        e.preventDefault();
        const newPet = {
        name,
        type,
        description,
        skill1,
        skill2,
        skill3
        }

        setName('');
        setType('');
        setDescription('');
        setSkill1('');
        setSkill2('');
        setSkill3('');

        axios.post("http://localhost:8000/api/pets/new", newPet)
        .then(res => {
            navigate(`/`);
        }).catch(err => {
            console.log(err.response);
            setErrors(err.response?.data?.errors)
        })
    }

    return (
        <div className="w-50 p-4 rounded mx-auto shadow">
            <div>
                <h3 className="text-center">Know a pet needing a home?</h3>

                <form
                    onSubmit={e => { createPet(e) }}
                >
                    <div className="form-group">
                    <label className="h6">Title</label>
                    {
                        errors?.title && (
                        <p style={{ color: 'red' }}>{errors.title?.message}</p>
                        )
                    }
                    <input
                        onChange={(event) => {
                        setTitle(event.target.value);
                        }}
                        type="text" className="form-control"
                    />
                    </div>

                    <div className="form-group">
                    <label className="h6">Description</label>
                    {
                        errors?.description && (
                        <p style={{ color: 'red' }}>{errors.description?.message}</p>
                        )
                    }
                    <textarea
                        onChange={(event) => {
                        setDescription(event.target.value);
                        }}
                        type="text"
                        className="form-control"
                    ></textarea>
                    </div>

                    <div className="form-group">
                    <label className="h6">Price</label>
                    {
                        errors?.price && (
                        <p style={{ color: 'red' }}>{errors.price?.message}</p>
                        )
                    }
                    <input
                        onChange={(event) => {
                        setPrice(event.target.value);
                        }}
                        type="text"
                        className="form-control"
                    />
                    </div>
                    <button className="btn btn-sm btn-outline-success">Submit</button>
                </form>
            </div>
            <p></p>
            <div className="text-center">
                <h2>All Products</h2>
                    {products.map((product) => {
                    // const { _id, title, price, description } = product;
                    return (
                        <div key={product._id} className="shadow mb-4 rounded border p-4">
                            <Link to={`/${product._id}`}><h4>{product.title}</h4></Link>
                        </div> 
                    );
                })}
            </div>
        </div>
    )
};

export default NewPet;
