import axios from "axios";
import { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";

export const NewPet = (props) => {
    const [title, setTitle] = useState('');
    const [price, setPrice] = useState('');
    const [description, setDescription] = useState('');
    const [errors, setErrors] = useState(null);
    const [products, setProducts] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        axios.get("http://localhost:8000/")
            .then(res => {
                console.log(res.data);
                setProducts(res.data);
            }).catch(err => {
                console.log(err);
            })
    }, [])

    const createProduct = async (e) => {
        e.preventDefault();
        const newProduct = {
        title,
        price,
        description
        }

        setTitle('');
        setPrice('');
        setDescription('');

        axios.post("http://localhost:8000/", newProduct)
        .then(res => {
            navigate(`/`);
            window.location.reload(false);
            // navigate(`/${res.data._id}`);
        }).catch(err => {
            /*
            For this catch to happen, the server needs to return an error status code.
            `?.` is optional chaining to safely access keys that may not exist.
            `errors` exists on validation errors but maybe not on other errors.
            */
            console.log(err.response);
            setErrors(err.response?.data?.errors)
        })
    }

    return (
        <div className="w-50 p-4 rounded mx-auto shadow">
            <div>
                <h3 className="text-center">New Product</h3>

                <form
                    onSubmit={e => { createProduct(e) }}
                >
                    <div className="form-group">
                    <label className="h6">Title</label>
                    {
                        errors?.title && (
                        <p style={{ color: 'red' }}>{errors.title?.message}</p>
                        )
                    }
                    <input
                        onChange={(event) => {
                        setTitle(event.target.value);
                        }}
                        type="text" className="form-control"
                    />
                    </div>

                    <div className="form-group">
                    <label className="h6">Description</label>
                    {
                        errors?.description && (
                        <p style={{ color: 'red' }}>{errors.description?.message}</p>
                        )
                    }
                    <textarea
                        onChange={(event) => {
                        setDescription(event.target.value);
                        }}
                        type="text"
                        className="form-control"
                    ></textarea>
                    </div>

                    <div className="form-group">
                    <label className="h6">Price</label>
                    {
                        errors?.price && (
                        <p style={{ color: 'red' }}>{errors.price?.message}</p>
                        )
                    }
                    <input
                        onChange={(event) => {
                        setPrice(event.target.value);
                        }}
                        type="text"
                        className="form-control"
                    />
                    </div>
                    <button className="btn btn-sm btn-outline-success">Submit</button>
                </form>
            </div>
            <p></p>
            <div className="text-center">
                <h2>All Products</h2>
                    {products.map((product) => {
                    // const { _id, title, price, description } = product;
                    return (
                        <div key={product._id} className="shadow mb-4 rounded border p-4">
                            <Link to={`/${product._id}`}><h4>{product.title}</h4></Link>
                        </div> 
                    );
                })}
            </div>
        </div>
    )
};

export default NewPet;
